import React from 'react'


export default function QuestionF({handleNextQuestion, handlePreviousQuestion, data,i }) {

    let obj = data(i);
    let id = obj.id;
    let question = obj.question;
    let options = obj.options;
    // const [checkedAnswer, setCheckedAnswer] = useState('')
   
    

    const onChangeValue = (event) => {
        var result = event.target.value;
        // setCheckedAnswer(result);
        console.log(result);
      } 
    //   console.log(handleAnswer);
    // selectedAnswer(checkedAnswer)    

    //Rendering
    return (
        <div>

            <h5 className="card-title">Question No. <span>| {id} </span></h5>
            <hr />
            <table className="table table-borderless">
                <thead>
                    <td><a href="//" className="text-primary fw-bold">{question}</a></td> 
                </thead>
                <br></br>
                <tbody >
                    {options.map((option) => { 

                        return (
                            <tr onChange={onChangeValue}>
                                <td><input type="radio" name="a"  value={option} checked={false} ></input> <a className="text-primary fw-bold">{option}</a></td>
                            </tr>)
                    
                    })}

                </tbody>
            </table>
            <div >
                {(
                    <button className="btn btn-primary " style={{ 'position': 'absolute', 'bottom': '35px' }} onClick={handlePreviousQuestion}>Previous Question</button>

                )}
                <span style={{ 'margin-right': '15%' }}></span>
                {(
                    <button className="btn btn-primary " style={{ 'position': 'absolute', 'bottom': '35px' }} onClick={handleNextQuestion}>Next Question</button>

                )}
            </div>
        </div>
    )
}
