import React,{useEffect, useState} from 'react'
import Axios from 'axios'
import QuestionService from './QuestionService';
const API_URL = "http://localhost:8081/admin/question";

export default function ButtonGenerator(props) {

    const [questions, setQuestions] = useState([]);
    const [index, setIndex] = useState(0);

    useEffect(() => {
        Axios.get(API_URL)
            .then(res => res.data)
            .then(data => {
                const questions = data.map((question) => ({
                    ...question,
                    id: [question.questionId],
                    options: [question.option1, question.option2, question.option3, question.option4]
                }))
                setQuestions(questions)
                console.log(questions[0].questionId)
            });
    }, [])

    const newFunction = () => {
        return <QuestionService currIdx={index}></QuestionService>
        
    }


    const renderQuestion = (event) =>{
       const i = event.target.value
    //    setIndex(i);
       props.handler(i);
       
        
    }


    var i;
    const buttons = () => {
        const row = [];
        // const totalQue = ([5,4,3,2,1]);
        for ( i = 1; i <= questions.length ; i++) {
          
            // row.push(<p key={i}>{i}</p>);
            row.push(<button className="btn btn-primary m-2" style={{ width: '45px' }} key={i} onClick={renderQuestion} value={i}>{i}</button>);
        }
        return row;
    };

    return (
        <div>
        <div>
            {
                buttons()
            }
        </div>
        {/* <div>
            {newFunction()}
        </div> */}
        </div>
    );
}
