import React, { useState, useEffect } from 'react'

const Question_Rest_Url = "http://localhost:8081/admin/question";

export default function QuestionF() {

    // second method
    const [questions, setQuestions] = useState([]);
    const [currentIndex, setCurrentIndex] = useState([0])
    useEffect(() => {
        fetch(Question_Rest_Url)
            .then((res) => res.json())
            .then((data) => {
                setQuestions(data)
            });
    }, []);

    //Next question 
    const handleNextQuestion = () => {
        setCurrentIndex(currentIndex + 1)

    }


    // Option generator
    const Button = ({ answer }) => (
        <tr>
            <td><input type="radio" name="a" ></input> <a className="text-primary fw-bold">{answer}</a></td>
        </tr>
    )

    //Rendering
    return questions.length > 0 ? (
        <div>

            <h5 className="card-title">Question No. <span>| {questions[0].questionId} </span></h5>
            <hr />
            <table className="table table-borderless">
                <thead>
                    <td><a href="//" className="text-primary fw-bold">{questions[0].question}</a></td>
                </thead>
                <br></br>
                <tbody>
                    <Button answer={questions[0].option1} />
                    <Button answer={questions[0].option2} />
                    <Button answer={questions[0].option3} />
                    <Button answer={questions[0].option4} />
                </tbody>
            </table>
            <div >
                <button className="btn btn-primary " style={{ 'position': 'absolute', 'bottom': '35px' }}>Previous Question</button>
                <span style={{ 'margin-right': '20%' }}></span>
                <button className="btn btn-primary" style={{ 'position': 'absolute', 'bottom': '35px' }}>Next Question</button>
            </div>
        </div>
    ) : (
        <h1> Hey Bro .... We are loading</h1>
    );
}
