import React, { useState, useEffect } from 'react'

const Question_Rest_Url = "http://localhost:8081/admin/question";

export default function QuestionF() {

    // second method
    const [questions, setQuestions] = useState([]);
    const [currentIndex, setCurrentIndex] = useState([0])
    useEffect(() => {
        fetch(Question_Rest_Url)
            .then((res) => res.json())
            .then((data) => {
                setQuestions(data.currentIndex)
            });
    }, []);

    //Next question 
    const handleNextQuestion = () =>{
        setCurrentIndex(currentIndex + 1)
    }


    // Option generator
    const Button = ({ answer }) => (
        <tr>
            <td><input type="radio" name="a" ></input> <a className="text-primary fw-bold">{answer}</a></td>
        </tr>
    )

    //Rendering
    return questions.length > 0 ? (
        <div>

            <h5 className="card-title">Question No. <span>| {questions.questionId} </span></h5>
            <hr />
            <table className="table table-borderless">
                <thead>
                    <td><a href="//" className="text-primary fw-bold">{questions.question}</a></td>
                </thead>
                <br></br>
                <tbody>
                    <Button answer={questions.option1} />
                    <Button answer={questions.option2} />
                    <Button answer={questions.option3} />
                    <Button answer={questions.option4} />
                </tbody>
            </table>
        </div>
    ) : (
        <h1> Hey Bro .... We are loading</h1>
    );
}
