import React from 'react'
import { useEffect,useState } from 'react';
import Timer from './Timer'
import { useNavigate } from 'react-router-dom';
//import Question from './Question';
//import QuestionService from './QuestionService';
import QuestionF from './component/QuestionF';

const Question_Rest_Url = "http://localhost:8081/admin/question";

export function ButtonGenerator() {
    const buttons = () => {
        const row = [];
        // const totalQue = ([5,4,3,2,1]);
        for (var i = 1; i <= 25; i++) {
            // row.push(<p key={i}>{i}</p>);
            row.push(<button className="btn btn-primary m-2" style={{ width: '45px' }} key={i}>{i}</button>);

        }
        return row;
    };

    return (
        <div>
            {
                buttons()
            }
        </div>
    );
}

export default function Exam() {
    const navigate = useNavigate();
    let onTimesup = () => {
        navigate("/");
    }

    const [questions, setQuestions] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0)
    useEffect(() => {
        fetch(Question_Rest_Url)
            .then((res) => res.json())
            .then((data) => {
                setQuestions(data)
            });
    }, []);

    //Next question 
    const handleNextQuestion = () =>{
        setCurrentIndex(currentIndex + 1)
    }

    return (

        <div>
            <header id="header" className="header fixed-top d-flex align-items-center">
                <div className="d-flex align-items-center justify-content-between">
                    <h4 style={{ color: 'white' }}>Web Programming Technology</h4>
                </div>
                <div className="header-nav ms-auto pe-5" >
                    <h4 style={{ color: 'white' }}>Time Left :  <Timer
                        onTimesup={onTimesup}
                        duration={3600}
                    /></h4>
                </div>
            </header>
            <main id="main" className="main" style={{ 'margin-right': '0px', 'margin-left': '20px' }}>

                <section className="section dashboard">
                    <div className="row">
                        {/* Left side card */}
                        <div className="col-lg-8">
                            <div className="row">

                                <div className="col-12">
                                    <div className="card top-selling overflow-auto">

                                        <div className="card-body pb-0 overflow-auto" style={{ 'height': '720px' }} >
                                            <QuestionF></QuestionF>


                                            {/* <div style={{ 'margin-top': '31%', 'margin-bottom': '03%', 'margin-left': '70%' }}> 
                                            
                                                <button className="btn btn-primary " style={{'position':'absolute' ,'bottom':'35px'}} >Previous Question</button>
                                                
                                                <span style={{ 'margin-left': '5%' }}></span>
                                                
                                                <button className="btn btn-primary " style={{'position':'absolute' ,'bottom':'35px'}} >Next Question</button>
                                            </div>  */}
                                            <div >
                                                <button className="btn btn-primary " style={{ 'position': 'absolute', 'bottom': '35px'}} onClick= {handleNextQuestion}>Previous Question</button>
                                                <span style={{ 'margin-right': '20%' }}></span>
                                                <button className="btn btn-primary" style={{ 'position': 'absolute', 'bottom': '35px'}}>Next Question</button>
                                            </div>

                                        </div>

                                    </div>
                                </div>


                            </div>
                        </div>

                        {/* Right side card */}
                        <div className="col-lg-4">
                            <div className="row">


                                <div className="col-12">
                                    <div className="card top-selling " >

                                        <div className="card-body pb-0" style={{ 'height': '720px' }}>
                                            <br />
                                            <h4 className="card-title" style={{ 'background-color': '#e1e1f3' }}>Student Name: Chandrakant Padme</h4>
                                            <h4 className="card-title" style={{ 'background-color': '#e1e1f3' }}>PRN No : 210945920064</h4>

                                            <hr />
                                            <h5 className="card-title" style={{ 'background-color': '#e1e1f3' }}>Questions </h5>

                                            <table className="table table-borderless">

                                            </table>
                                            <div style={{ 'overflow': 'auto', 'height': '350px' }}>

                                                <ButtonGenerator></ButtonGenerator>
                                            </div>
                                            <hr />

                                            <button className="btn btn-success" style={{ 'position': 'absolute', 'bottom': '35px' }}><a href="index.html" style={{ 'color': 'white', 'text-decoration': 'none' }}>Submit Exam</a></button>

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </section>

            </main>


        </div>

    )
}
